import React, { useState } from "react";

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async () => {
    const endpoint = isLogin ? "login" : "register";

    // payload matches backend (uses name, not username)
    const payload: any = { name, password };
    if (!isLogin) payload.email = email;

    try {
      const res = await fetch(`http://localhost:5000/api/auth/${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();

      if (res.ok) {
        alert(`${isLogin ? "Logged in" : "Account created"} successfully`);
      } else {
        alert(data.msg || "Something went wrong");
      }
    } catch (err) {
      console.error("Error submitting form:", err);
      alert("Server error. Check console for details.");
    }
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "400px", margin: "auto" }}>
      <h1>{isLogin ? "Login" : "Sign Up"}</h1>
      <button onClick={() => setIsLogin(!isLogin)} style={{ marginBottom: "1rem" }}>
        Switch to {isLogin ? "Sign Up" : "Login"}
      </button>
      <div style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
        {/* ðŸ”¹ fixed: using name, not username */}
        <input placeholder="Name" value={name} onChange={e => setName(e.target.value)} />
        {!isLogin && (
          <input placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
        )}
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button onClick={handleSubmit}>{isLogin ? "Login" : "Sign Up"}</button>
      </div>
    </div>
  );
};

export default Login;
