import React, { useState } from "react";
import "../App.css";

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState(""); // <-- For backend message

  const handleSubmit = async () => {
    const endpoint = isLogin ? "login" : "register";
    const payload = isLogin ? { email, password } : { name, email, password };

    try {
      const res = await fetch(`http://localhost:3000/api/auth/${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();

      // Show backend message directly
      setMessage(data.msg || (res.ok ? `${isLogin ? "Logged in" : "Account created"} successfully` : "Something went wrong"));
    } catch (err) {
      console.error(err);
      setMessage("Server error. Check console for details.");
    }
  };

  return (
    <div className="login-container">
      <h1>{isLogin ? "Login" : "Sign Up"}</h1>
      <button onClick={() => setIsLogin(!isLogin)}>
        Switch to {isLogin ? "Sign Up" : "Login"}
      </button>
      <div className="login-form">
        {!isLogin && <input placeholder="Name" value={name} onChange={e => setName(e.target.value)} />}
        <input placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
        <button onClick={handleSubmit}>{isLogin ? "Login" : "Sign Up"}</button>
      </div>

      {/* Display backend message */}
      {message && <p className="backend-message">{message}</p>}
    </div>
  );
};

export default Login;
