import React, { useEffect, useState } from "react";
import { Product } from "../types";
import { Link } from "react-router-dom";

const Products = () => {
  const [products, setProducts] = useState<Product[]>([]);

  useEffect(() => {
    fetch("http://localhost:3000/api/products") // adjust your backend URL
      .then(res => res.json())
      .then(data => setProducts(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Products</h1>
      <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "1rem", marginTop: "1rem" }}>
        {products.map(p => (
          <div key={p.id} style={{ border: "1px solid #ccc", borderRadius: "8px", padding: "1rem" }}>
            <img src={p.image_url} alt={p.title} style={{ width: "100%", height: "150px", objectFit: "cover" }} />
            <h3>{p.title}</h3>
            <p>{p.description}</p>
            <p><strong>Price:</strong> ${p.price}</p>
            <p><strong>Stock:</strong> {p.quantity}</p>

          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
